import React from "react";

class MouseEvent extends React.Component{
    constructor(props){
        super(props);
        this.state={
            x: 0,
            y: 0
        }
        this.Coordinates= this.Coordinates.bind(this);
    }

    Coordinates(event){
        setInterval(()=>{
            this.setState({x: event.screenX , y: event.screenY});
        },3000);
        
    }

    render(){

        return(
            
            <div onMouseMove={this.Coordinates(this)}>
                {this.state.x}
                {this.state.x}
            </div>
        )
    }

}

export default MouseEvent;
