class ExampleComponent extends React.Component{
    constructor(props) {
        super(props);
        this.state={
            ping:1
        }
        this.Call = this.Call.bind(this);
    }

    Call(){
        this.setState({ping: this.state.ping+1})
        //alert("You called "+this.state.ping+" times");
    }

    render(){
        var textStyle={
            fontSize:72,
            fontFamily:"sans-serif",
            color:"#333",
            fontWeight:"bold",
            textAlign:"center",
        }

        var bgStyle={
            padding:50,
            backgroundColor:"#FFC53A",
            borderRadius:10,
            width: 250,
            height: 100,
            textAlign:"center",
        }

        var btnStyle={
            width:30,
            height:30,
            color:"#333",
            fontWeight:"bold",
            fontSize:"1em",
            lineHeight:"3px"

        };
        return(
            <div style={textStyle}>
                <h3 >Counter</h3>
                {this.state.ping}<br/>
                <button onClick={this.Call}>Click</button>
            </div>
        )
    }




}

export default ExampleComponent;
