import React from "react";

class MouseEvent extends React.Component{
    constructor(props){
        super(props);
        this.state={
            x: 0
        }
    }

    Coordinates(event){
        this.setState({x: event.clientX, y:event.clientY})
    }

    render(){

        return(
            <div onmousemove="Coordinates(event)"></div>
        )
    }

}

export default MouseEvent;
