import React from "react";
class Test extends React.Component{
    constructor(props){
        super(props)
        this.state={
            brand:"Ford",
            color:"Red",
            year:2012,
            name:"Ecosport"
        };
    }
    render(){
        return(
            <div>
                <p>
                {this.state.brand}
                {this.state.color}
                {this.state.year}
                {this.state.color}

                </p>
            </div>
        )
    }


}

export default Test;
