import React from "react";
class OrderedList extends React.Component{
    constructor(props){
        super(props);
        this.state={
            Name: props.name,
            Description: props.desc,
            Price: props.price,
            Quantity: props.quantity
        }
        this.handleClick= this.handleClick.bind(this);
    } 
    handleClick(){
        this.setState({Quantity: this.state.Quantity+1, Price: this.state.Quantity*this.state.Price})
    } 
    
    render(){


        return(

            <div>
                <ul>
                    <li>{this.state.Name}</li>
                    <li>{this.state}</li>

                </ul>
                
                
            </div>
        )
    }
}

export default OrderedList;
